#import <Foundation/Foundation.h>#define DEBUG_FILE                    ([[[[NSString alloc] initWithCString:__FILE__ encoding:NSUTF8StringEncoding] autorelease] lastPathComponent])#define DEBUG_FUNC                    (__FUNCTION__)#define DEBUG_LINE                    (__LINE__)#ifdef __cplusplusextern "C"{#endifvoid saveLog(NSString *log);void printLog(NSString *file, int line, NSString *fmt, ...);#ifdef __cplusplus}#endif#define LogInfo(fmt, ...)  printLog(DEBUG_FILE, DEBUG_LINE, fmt, ##__VA_ARGS__);